mapper.select:
SELECT DISTINCT tx_id
FROM amz_TCC
ORDER BY create_at limit 1;

SELECT *
FROM amz_TCC
WHERE tx_id = #{txId}
  AND tried_times <= 3
    FOR
        UPDATE;

mapper.update:
UPDATE amz_TCC
SET branch_status = #{branchStatus}
WHERE id = #{branchId}

UPDATE amz_TCC
SET executor = #{executor}
WHERE tx_id = #{txId}

UPDATE amz_TCC
SET executor = null
WHERE tx_id = #{txId}

UPDATE amz_TCC
SET tried_times = tried_times + 1
WHERE tx_id = #{txId}

UPDATE amz_TCC
SET tried_times = 0
WHERE tx_id = #{txId}
